import React, { Component } from "react";
import Cards from "../components/Cards";
import "./App.css";
import CardList from "../components/CardList";
import data from "../components/data/db.json";

import LeftArrow from "../components/LeftArrow";
import RightArrow from "../components/RightArrow";
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      properties: data.cards,
      property: data.cards[0]
    };
  }

  nextProperty = () => {
    const newIndex = this.state.property.id + 1;
    this.setState({
      property: data.cards[newIndex]
    });
    console.log(data.cards[newIndex]);
  };

  prevProperty = () => {
    const newIndex = this.state.property.id - 1;
    this.setState({
      property: data.cards[newIndex]
    });
  };

  render() {
    const { properties, property } = this.state;
    console.log(properties.length);
    return (
      <div className="App">
        <button
          onClick={() => this.nextProperty()}
          disabled={property.id === data.cards.length - 1}
        >
          Next
        </button>
        <button onClick={() => this.prevProperty()}>Prev</button>

        <div className="card-slider">
          <div className="card-slider-wrapper">
            <Cards
              key={property.id}
              name={property.title}
              subtitle={property.subtitle}
              text={property.text}
              image_url={property.image_url}
              href={property.href}
              is_liked={property.is_liked}
            />
          </div>
        </div>
      </div>
    );
  }
}
export default App;
