import React, { Component } from "react";
import Cards from "../components/Cards";
import "./App.css";
import data from "../components/data/db.json";
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      properties: data.cards,
      s: 0,
      f: 3,
      property: data.cards[0]
    };
  }

  nextProperty = () => {
    const newIndex = this.state.property.index + 1;
    console.log(newIndex, this.state.s, this.state.f);
    this.setState({
      property: data.cards[newIndex],
      s: this.state.f,
      f: f + 3
    });
  };

  prevProperty = () => {
    const newIndex = this.state.property.index - 1;
    this.setState({
      property: data.cards[newIndex]
    });
  };

  render() {
    const { property, s, f } = this.state;
    var size = property.id + 2;

    return (
      <div className="App">
        <button
          onClick={() => this.nextProperty()}
          disabled={property.index === data.cards.length - 1}
        >
          Next
        </button>
        <button
          onClick={() => this.prevProperty()}
          disabled={property.index === 0}
        >
          Prev
        </button>

        <div className="page">
          {data.cards.slice(s, f).map((property, i) => (
            <div>
              <Cards
                key={i}
                name={property.title + property.id}
                subtitle={property.subtitle}
                text={property.text}
                image_url={property.image_url}
                href={property.href}
                is_liked={property.is_liked}
              />
            </div>
          ))}
        </div>
      </div>
    );
  }
}
export default App;
