import React, { Component } from "react";
import "./Cards.css";
import Like from "./assets/like.png";

class Cards extends Component {
  render() {
    const { name, text, subtitle, href, image_url } = this.props;
    return (
      <div className="card">
        <a href="">
          <div className="card-background">
            <img src={href} className="card-image" />
          </div>

          <div className="card-info">
            <div>
              {" "}
              <img src={image_url} className="card-profile-image" />
            </div>
            <div className="card1">
              <h1>{name}</h1>
              <h2>{subtitle}</h2>
            </div>
          </div>

          <div className="card-desc">
            <p>{text}</p>
          </div>
          <div className="like-button">
            <img src={Like} />
          </div>
        </a>
      </div>
    );
  }
}
export default Cards;
