import React, { Component } from "react";
import "./Cards.css";
import Like from "./assets/like.png";
import UnLike from "./assets/nolike.png";
import Man from "./assets/man.png";

class Cards extends Component {
  constructor() {
    super();
    this.state = {
      like: "like",
      liketext: ""
    };
  }
  myFunction() {
    if (this.state.like === "like") {
      this.setState({ liketext: "like" });
      console.log("licked");
    } else {
      this.setState({ liketext: "Unlike" });
      console.log("unlicked");
    }
  }
  render() {
    const { name, text, subtitle, href, image_url, is_liked } = this.props;

    return (
      <div className="card">
        <a href={href}>
          <div className="card-background">
            <img src={image_url} className="card-image" />
          </div>

          <div className="card-info">
            <div>
              {" "}
              <img src={Man} className="card-profile-image" />
            </div>
            <div className="card1">
              <h1>{name}</h1>
              <h2>{subtitle}</h2>
            </div>
          </div>

          <div className="card-desc">
            <p>{text}</p>
          </div>
        </a>
        <div className="like-button" onClick={this.myFunction}>
          this.state.like === "signin" ? (
          <img src={Like} />
          ) : (
          <img src={unLike} />)
        </div>
      </div>
    );
  }
}
export default Cards;
