import React, { Component } from "react";
import Cards from "../components/Cards";
import "./App.css";
import data from "../components/data/db.json";
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      properties: data.cards,
      property: data.cards[0]
    };
  }

  nextProperty = () => {
    var size = this.properties.length - 6;
    const newIndex = this.state.property.index + 1;
    this.setState({
      property: data.cards[newIndex]
    });
  };

  prevProperty = () => {
    const newIndex = this.state.property.index - 1;
    this.setState({
      property: data.cards[newIndex]
    });
  };

  render() {
    const { property, properties } = this.state;

    return (
      <div className="App">
        <button
          onClick={() => this.nextProperty()}
          disabled={property.index === data.cards.length - 1}
        >
          Next
        </button>
        <button
          onClick={() => this.prevProperty()}
          disabled={property.index === 0}
        >
          Prev
        </button>

        <div className="page">
          {data.cards.slice(0, size).map((property, index) => (
            <div>
              <Cards
                key={index}
                name={property.title}
                subtitle={property.subtitle}
                text={property.text}
                image_url={property.image_url}
                href={property.href}
                is_liked={property.is_liked}
              />
            </div>
          ))}
        </div>
      </div>
    );
  }
}
export default App;
