import React, { Component } from "react";
import Cards from "../components/Cards";
import "./App.css";
import CardList from "../components/CardList";
import data from "../components/data/db.json";

import LeftArrow from "../components/LeftArrow";
import RightArrow from "../components/RightArrow";
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      properties: data.cards,
      property: data.cards[0]
    };
  }

  nextProperty = () => {
    console.log(newIndex);
    const newIndex = this.state.property.id + 1;
    this.setState({
      property: data.cards[newIndex]
    });
    console.log(data.cards[newIndex]);
  };

  prevProperty = () => {
    const newIndex = this.state.property.id;
    this.setState({
      property: data.cards[newIndex]
    });
    console.log(data.cards[newIndex]);
  };

  render() {
    const { property } = this.state;
    return (
      <div className="App">
        <button onClick={() => this.nextProperty()}>Next</button>
        <button
          onClick={() => this.prevProperty()}
          disabled={property.index === 0}
        >
          Prev
        </button>

        <div className="page">
          <Cards property={property} />
        </div>
      </div>
    );
  }
}
export default App;
