import React, { Component } from "react";
import Cards from "../components/Cards";
import "./App.css";
import Slider from "../components/Slider";
import data from "../components/data/db.json";

import LeftArrow from "../components/LeftArrow";
import RightArrow from "../components/RightArrow";
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      properties: data.cards,
      property: data.cards[1]
    };
  }

  nextProperty = () => {
    const newIndex = this.state.property.id + 1;
    this.setState({
      property: data.cards[newIndex]
    });
  };

  prevProperty = () => {
    const newIndex = this.state.property.index - 1;
    this.setState({
      property: data.cards[newIndex]
    });
  };

  render() {
    const { properties, property } = this.state;
    return (
      <div className="App">
        <button
          onClick={() => this.nextProperty()}
          disabled={property.id === data.cards.length - 1}
        >
          Next
        </button>
        <button
          onClick={() => this.prevProperty()}
          disabled={property.id === 0}
        >
          Prev
        </button>

        <div className="page">
          <div className="col">
            {properties.map(property => (
              <Cards key={property.id} property={property} />
            ))}
          </div>
        </div>
      </div>
    );
  }
}
export default App;
